pub fn get_current_element_based_level(level: i8, x:usize, y: usize ) -> Pixel {
    match level {
        1 => {
            let ch = map_level_1(x, y);
            if '#' == ch {
                return Pixel{
                    ch: ch,
                    color: crossterm::style::Color::DarkBlue
                }
            } else if '.' == ch {
                return Pixel {
                    ch: ch,
                    color: crossterm::style::Color::White
                }
            }
            Pixel{
                ch: ch,
                color: crossterm::style::Color::White
            } 

        },
        _ => Pixel{
            ch: ' ',
            color: crossterm::style::Color::White
        } ,
    }
}